'use strict'
const leftArrow = document.querySelector('.controllers .left-arrow'),
			rightArrow = document.querySelector('.controllers .right-arrow'),
			blockWrapper = document.querySelector('.news_blocks-wrppaer'),
			blockWidth = blockWrapper.querySelector('.news_item').offsetWidth + 20;
var move = 0;

leftArrow.addEventListener('click' , () => {
	blockWrapper.style.transform = `translateX(${ move -= blockWidth }px)`;
});
rightArrow.addEventListener('click' , () => {
	blockWrapper.style.transform = `translateX(${ move += blockWidth }px)`;
});

const burgerMenu = document.querySelector(".burger-menu");
const heroNav = document.querySelector('.hero-nav');

burgerMenu.addEventListener("click", function() {
	this.classList.toggle("menu-on");
	heroNav.classList.toggle('hero-nav-active');
})

